# Function to close the overlay
def close_overlay(driver):
    try:
        close_button = WebDriverWait(driver, 5).until(
            EC.element_to_be_clickable((By.CLASS_NAME, "featherlight-close-icon"))
        )
        close_button.click()
    except TimeoutException:
        print("No overlay found or couldn't be closed.")

# Function to click the accordion button
def click_accordion(driver):
    try:
        accordion_button = WebDriverWait(driver, 2).until(
            EC.element_to_be_clickable((By.XPATH, "//h4[contains(text(), 'Yerleşenlerin Mezun Oldukları Liseler')]"))
        )
        accordion_button.click()
    except TimeoutException:
        print("Accordion button not found or couldn't be clicked.")

def click_acc(driver):
    try:
        accordion_button = WebDriverWait(driver, 2).until(
            EC.element_to_be_clickable((By.XPATH, "//h4[contains(text(), 'Yerleşen Son Kişinin Profili')]"))
        )
        accordion_button.click()

        table = WebDriverWait(driver, 2).until(
            EC.presence_of_element_located((By.CSS_SELECTOR, "table.table.table-bordered"))
        )
        
        # Extract rows from the table
        rows = table.find_elements(By.TAG_NAME, 'tr')
        
        # Create a dictionary to store the data
        success = {}
        
        if len(rows) >= 5:
            cols = rows[4].find_elements(By.TAG_NAME, 'td')  # Fifth row (index 4)
            if len(cols) >= 2:  # Ensure the second column exists
                # Get the second column value and convert it to an integer
                success = float(cols[1].text.strip())
                return success
        print("Fifth row or second column not found.")
        return None
    except TimeoutException:
        print("Timeout occurred while scraping the accordion table.")
        return None
    except NoSuchElementException:
        print("Required elements not found in the accordion table.")
        return None

# Function to scrape data from a single page
def scrape_page(url):
    driver.get(url)
    
    # Close the overlay if it exists
    close_overlay(driver)
    success = click_acc(driver)
    
    try:
        # Click the accordion button
        click_accordion(driver)
        
        # Wait for the table to appear
        table = WebDriverWait(driver, 2).until(
            EC.presence_of_element_located((By.CSS_SELECTOR, "table.table.table-bordered"))
        )
        
        # Extract rows from the table
        rows = table.find_elements(By.TAG_NAME, 'tr')
        
        # Create a dictionary to store the data
        high_school_data = {}
        
        # Process each row (skip the first two header rows)
        for row in rows[2:]:
            cols = row.find_elements(By.TAG_NAME, 'td')
            if len(cols) == 4:
                high_school_name = cols[0].text.strip()
                total = int(cols[1].text.strip())
                high_school_data[high_school_name] = total
        
        return high_school_data
    except TimeoutException:
        print(f"Timeout occurred while scraping {url}")
        return {}
    except NoSuchElementException:
        print(f"Required elements not found on {url}")
        return {}

# Set up Chrome driver
driver = webdriver.Chrome(service=Service(ChromeDriverManager().install()))


all_data = []
for i, link in enumerate(links, 1):
    print(f"Scraping link {i}/{len(links)}: {link}")
    page_data = scrape_page(link)
    print(f"Data scraped for {link}: {page_data}")

    if page_data:
        for key, value in page_data.items():
            all_data.append({'High School': key, 'Success': value})


